INSERT INTO `books` (`b_id`, `b_cate_id`, `b_name`, `b_slug`, `b_summary`, `b_img`, `b_author`, `b_view`, `b_active`, `created_at`, `updated_at`) VALUES
(1, 1, 'nụ hôn của sói', 'nu-hon-cua-soi', '<p>Nếu An Dĩ Phong kh&ocirc;ng t&iacute;nh l&agrave; đ&agrave;n &ocirc;ng, tr&ecirc;n thế giới n&agrave;y kh&ocirc;ng ai d&aacute;m n&oacute;i ch&iacute;nh m&igrave;nh l&agrave; đ&agrave;n &ocirc;ng!</p>\r\n\r\n<p>Nếu An Dĩ Phong kh&ocirc;ng t&iacute;nh l&agrave; y&ecirc;u nghiệt, như vậy, tr&ecirc;n thế giới n&agrave;y cũng kh&ocirc;ng hề c&oacute; y&ecirc;u nghiệt...</p>\r\n\r\n<p>Hắn l&agrave; một người đ&agrave;n &ocirc;ng như vậy, rong ruổi giang hồ mười lăm năm, ai d&aacute;m c&ugrave;ng hắn một c&acirc;u tr&aacute;i &yacute;, về sau đừng nghĩ mở miệng n&oacute;i chuyện. Hắn ki&ecirc;u ngạo ương ngạnh, ho&agrave;nh h&agrave;nh ngang dọc, hắn c&ocirc; độc, mệt mỏi... nhưng mấy ai biết rằng, trong tim hắn chỉ c&oacute; một b&oacute;ng h&igrave;nh, v&agrave; c&oacute; một người lặng lẽ y&ecirc;u hắn, chờ hắn... ở một nơi rất xa.</p>\r\n\r\n<p>H&agrave;i hước, l&atilde;ng mạn, mi&ecirc;u tả t&acirc;m l&yacute; nh&acirc;n vật cực kh&eacute;o, cốt truyện gay cấn, t&aacute;c giả đ&atilde; tạo cho c&acirc;u chuyện t&igrave;nh y&ecirc;u đầy m&agrave;u sắc cổ t&iacute;ch giữa một nữ cảnh s&aacute;t v&agrave; anh ch&agrave;ng lừng lẫy chốn giang hồ sự hấp dẫn đặc biệt. Đan xen v&agrave;o c&acirc;u chuyện t&igrave;nh y&ecirc;u n&agrave;y l&agrave; c&acirc;u chuyện của t&igrave;nh bạn, t&igrave;nh anh em, t&igrave;nh cha con, nghĩa kh&iacute;, chữ t&iacute;n v&agrave; sức mạnh của những ước mơ. Đ&oacute; ch&iacute;nh l&agrave; những điều tốt đẹp trong cuộc đời n&agrave;y.</p>\r\n\r\n<p>&ldquo;Em kh&ocirc;ng cần kết quả, cũng kh&ocirc;ng cần hứa hẹn g&igrave;, c&oacute; thể y&ecirc;u th&ecirc;m được một ng&agrave;y th&igrave; sẽ y&ecirc;u một ng&agrave;y, c&oacute; thể nh&igrave;n th&ecirc;m được một lần th&igrave; sẽ nh&igrave;n th&ecirc;m một lần!&rdquo;</p>\r\n\r\n<p>&ldquo;Tư Đồ Thuần, em h&atilde;y nhớ rằng, An Dĩ Phong cả đời n&agrave;y sẽ kh&ocirc;ng lấy ai kh&aacute;c ngo&agrave;i em!&rdquo;</p>\r\n\r\n<p>&ldquo;Nhưng em kh&ocirc;ng thể lấy anh.&rdquo;</p>\r\n\r\n<p>&ldquo;Anh biết&hellip; Được y&ecirc;u em l&agrave; đủ rồi.&rdquo;</p>\r\n\r\n<p>&ldquo;Cho d&ugrave; ng&agrave;y mai c&oacute; kết cục thế n&agrave;o đi nữa, em cũng sẽ kh&ocirc;ng hối hận v&igrave; đ&atilde; y&ecirc;u anh.&rdquo;</p>\r\n\r\n<p>- Tr&iacute;ch: Nụ h&ocirc;n của s&oacute;i</p>', '30-nu-hon-cua-soi.jpg', 'diệp lạc vô tâm', 22, 1, '2021-06-21 08:35:21', '2021-06-28 03:31:59'),
(2, 1, 'quân hôn chớp nhoáng', 'quan-hon-chop-nhoang', '<p>Mối t&igrave;nh thanh xu&acirc;n d&agrave;i bảy năm của Kiều T&acirc;m Duy kết th&uacute;c với sự phản bội của người y&ecirc;u c&ugrave;ng người bạn th&acirc;n của m&igrave;nh. Cũng chỉ v&igrave; muốn mọi người thấy c&ocirc; vẫn ổn m&agrave; c&ocirc; đ&atilde; chấp nhận đi xem mắt hết lần n&agrave;y tới lần kh&aacute;c.</p>\r\n\r\n<p>Nam ch&iacute;nh l&agrave; Giang Hạo c&oacute; xuất th&acirc;n l&agrave; Thủ trưởng qu&acirc;n đội v&agrave; l&agrave; một người đ&agrave;n &ocirc;ng ki&ecirc;u ngạo. Sau sự ra đi của người y&ecirc;u cũ c&aacute;ch đ&acirc;y năm năm, Giang Hạo vẫn chưa chấp nhận một c&ocirc; g&aacute;i n&agrave;o ở b&ecirc;n cạnh d&ugrave; gia đ&igrave;nh đ&atilde; giục gi&atilde; việc h&ocirc;n nh&acirc;n nhiều lần.</p>\r\n\r\n<p>T&igrave;nh cờ gặp gỡ v&agrave;i lần v&agrave; v&ocirc; thức ch&uacute; &yacute; tới nhau, hai người nhanh ch&oacute;ng tiến tới h&ocirc;n nh&acirc;n v&igrave; nhận ra đối phương c&oacute; thể mang lại lợi &iacute;ch cho m&igrave;nh.</p>\r\n\r\n<p>- ----</p>\r\n\r\n<p>Giang Hạo: &ldquo;T&ocirc;i thấy em hợp mắt, em c&oacute; d&aacute;m đi đăng k&yacute; kết h&ocirc;n với t&ocirc;i b&acirc;y giờ kh&ocirc;ng?&rdquo;</p>\r\n\r\n<p>Kiều T&acirc;m Duy: &ldquo;Đồ thần kinh.&rdquo;</p>\r\n\r\n<p>Giang Hạo: &ldquo;T&ocirc;i n&oacute;i thật!&rdquo;</p>\r\n\r\n<p>Kiều T&acirc;m Duy: &ldquo;Cục D&acirc;n ch&iacute;nh nghỉ l&agrave;m rồi.&rdquo;</p>\r\n\r\n<p>Giang Hạo: &ldquo;Thế th&igrave; ng&agrave;y mai. Ch&iacute;n giờ s&aacute;ng mai, gặp nhau tại Cục D&acirc;n ch&iacute;nh, kh&ocirc;ng gặp kh&ocirc;ng về, em d&aacute;m kh&ocirc;ng?&rdquo;</p>\r\n\r\n<p>Kiều T&acirc;m Duy do dự: &ldquo;Anh kh&ocirc;ng n&oacute;i đ&ugrave;a? Ch&uacute;ng ta sẽ đi đăng k&yacute; thật &agrave;?&rdquo;</p>\r\n\r\n<p>Giang Hạo: &ldquo;Nếu ch&uacute;ng ta kh&ocirc;ng t&igrave;m thấy người th&iacute;ch hợp, sao kh&ocirc;ng cho đối phương một cơ hội chứ?&rdquo;</p>', '99-quan-hon-chop-nhoang.jpg', 'ngư ca', 11, 1, '2021-06-21 08:36:48', '2021-06-28 07:04:30'),
(3, 1, 'phượng kinh thiên', 'phuong-kinh-thien', '<p>Thật sự kh&ocirc;ng thể ngờ rằng chỉ l&agrave; nhắm mở mắt lần nữa đ&atilde; l&agrave;m cho số mệnh của Lam V&acirc;n, V&ocirc; Ưu, Nh&acirc;n L&atilde;nh&hellip; đ&atilde; chuyển dời.</p>\r\n\r\n<p>Điều g&igrave; sẽ xảy ra nếu một ng&agrave;y n&agrave;o đ&oacute; c&oacute; một người phụ nữ đ&atilde; trưởng th&agrave;nh sau khi mở mắt ph&aacute;t hiện bản th&acirc;n đang sở hữu th&acirc;n thể của một đứa trẻ bảy tuổi? Cảm x&uacute;c c&ocirc; sẽ như thế n&agrave;o khi đ&oacute;n chờ c&ocirc; kh&ocirc;ng phải th&acirc;n phận c&ocirc;ng ch&uacute;a cao qu&yacute; v&ocirc; thượng m&agrave; l&agrave; ba chữ Nh&acirc;n L&atilde;nh cung xa lạ, l&agrave; th&aacute;nh lệnh giam giữ cả đời kh&ocirc;ng được miễn x&aacute;? V&agrave;, c&acirc;u chuyện sẽ ra sao khi người phụ nữ ấy kh&ocirc;ng cam t&acirc;m chấp nhận c&aacute;i kết người ta đặt sẵn cho m&igrave;nh?</p>\r\n\r\n<p>Đau đớn, th&ugrave; hận đ&atilde; từng biến một người phụ nữ dịu d&agrave;ng, nhu m&igrave;, cam chịu th&agrave;nh người t&acirc;m cơ, &acirc;m hiểm. V&igrave; y&ecirc;u m&agrave; sinh hận, đ&oacute; l&agrave; lẽ dĩ nhi&ecirc;n ở đời.</p>\r\n\r\n<p>Tuy nhi&ecirc;n ngược d&ograve;ng thời gian về h&agrave;ng trăm năm trước, giữa cung điện nguy nga tr&aacute;ng lệ, giữa l&ograve;ng Trung cung mu&ocirc;n v&agrave;n gian kế, chữ y&ecirc;u ấy liệu c&oacute; phải l&agrave; tất cả? Tự do. Ngẫm nghĩ mấy hồi th&igrave; m&aacute;u mủ t&igrave;nh th&acirc;m, trung thần ch&iacute; nghĩa cũng chẳng l&agrave; g&igrave; trước sức mạnh trước mảnh trời xanh ngắt, rộng lớn ngo&agrave;i kia.</p>\r\n\r\n<p>Trước l&agrave; Lam V&acirc;n, sau l&agrave; V&ocirc; Ưu, b&ecirc;n cạnh c&oacute; Cố Th&aacute;i Phi, Ho&agrave;i Vương, Ngọc Ch&acirc;u, Ngọc Th&uacute;y, Tiểu Hoa Tử, Tiểu Cao Tử v&agrave; h&agrave;ng trăm, h&agrave;ng vạn nh&acirc;n mạng trong cung cấm n&agrave;y tựa như đều đang v&ugrave;ng vẫy để t&igrave;m được hai chữ tự do cho ri&ecirc;ng m&igrave;nh.</p>\r\n\r\n<p>Liệu rằng, với tr&iacute; tuệ của một người phụ nữ am hiểu kinh thư như Lam V&acirc;n, với gương mặt kinh diễm khuynh nước khuynh th&agrave;nh của V&ocirc; Ưu c&ocirc;ng ch&uacute;a, c&ugrave;ng với những g&igrave; Lưu Thị O&aacute;nh Hoa đ&atilde; để lại, thế cờ vận mệnh n&agrave;y c&oacute; được lật lại? Tiếng đ&agrave;n của V&ocirc; Ưu c&oacute; kinh động được tới Cố Th&aacute;i Phi? Nh&acirc;n L&atilde;nh cung rốt cuộc c&oacute; phải l&agrave; nơi đ&atilde; v&agrave;o th&igrave; kh&ocirc;ng thể trở ra?</p>', '97-phuong-kim-thien.jpg', 'lạc tùy tâm', 14, 1, '2021-06-21 08:38:03', '2021-06-28 03:12:17'),
(4, 2, 'thư kiếm trường an', 'thu-kiem-truong-an', '<p>Đ&atilde; hai năm từ khi Mạc Th&iacute;nh Vũ một m&igrave;nh một đao rời khỏi Trường An hướng về phương Bắc. H&ocirc;m nay, thiếu ni&ecirc;n T&ocirc; Trường An mang b&ecirc;n m&igrave;nh một c&acirc;y đao tiến v&agrave;o Trường An. Từ đ&acirc;y c&acirc;u chuyện của ch&uacute;ng ta bắt đầu...</p>', '96-thu-kiem-truong-an-han-tung-la-thieu-nien.jpg', 'hắn từng là thiếu niên', 15, 1, '2021-06-21 08:39:19', '2021-06-28 03:49:58'),
(5, 2, 'giang hồ dị giới', 'giang-ho-di-gioi', '<p>Hắn - Quảng Mục Thi&ecirc;n, một th&acirc;n v&otilde; thuật si&ecirc;u cường được giang hồ đặt biệt danh &quot;Cửu Long&quot;<br />\r\n<br />\r\nV&igrave; giữ lời hứa với Minh chủ v&otilde; l&acirc;m năm xưa m&agrave; hắn đ&atilde; đứng ra bảo vệ giang hồ khi Ma gi&aacute;o c&oacute; &yacute; đồ th&acirc;u t&oacute;m.<br />\r\n<br />\r\nKhi Quảng Mục Thi&ecirc;n v&agrave; L&atilde;nh Mạc T&agrave; giao đấu th&igrave; bất ngờ c&oacute; một lỗ đen h&uacute;t hắn v&agrave;o khiến hắn xuy&ecirc;n đến thế giới ma thuật.<br />\r\n<br />\r\nĐiều kỳ lạ l&agrave; kh&ocirc;ng chỉ c&oacute; m&igrave;nh hắn m&agrave; to&agrave;n bộ v&otilde; l&acirc;m đều xuy&ecirc;n qua.<br />\r\n<br />\r\nMọi chuyện rồi sẽ ra sao? Ma chủ lại t&aacute;i xuất giang hồ với d&atilde; t&acirc;m c&ograve;n lớn hơn. Mời c&aacute;c bạn đ&oacute;n xem.&nbsp;</p>', '83-giang-ho-di-gioi-cuu-long.jpg', 'cửu long', 15, 1, '2021-06-21 08:40:23', '2021-06-28 03:53:21'),
(10, 1, 'cô vợ ngọt ngào có chút bất lương', 'co-vo-ngot-ngao-co-chut-bat-luong', '<p><strong>T&ecirc;n kh&aacute;c:&nbsp;</strong>C&ocirc; Vợ Bất Lương C&oacute; Ch&uacute;t Ngọt Ng&agrave;o<br />\r\n<br />\r\n&quot;Khẩu vị của người n&agrave;y rốt cuộc ra sao a! C&aacute;i n&agrave;y cũng bỏ được v&agrave;o miệng &agrave;?&quot;<br />\r\n<br />\r\nSau khi c&ocirc; tỉnh dậy, nh&igrave;n v&agrave;o trong gương thấy ch&iacute;nh m&igrave;nh đầu xăm mặt giống như quỷ, cảm gi&aacute;c chỉ nh&igrave;n th&ecirc;m một gi&acirc;y cũng hỏng đ&ocirc;i mắt.<br />\r\n<br />\r\nTrước khi trọng sinh, Cố Việt Trạch ch&iacute;nh l&agrave; người m&agrave; c&ocirc; d&ugrave;ng cả tấm l&ograve;ng để y&ecirc;u nhưng sau đ&oacute; cũng l&agrave; người m&agrave; c&ocirc; hận thấu xương.<br />\r\n<br />\r\nĐời trước c&ocirc; ch&iacute;nh l&agrave; kiểu phụ nữ n&atilde;o t&agrave;n n&ecirc;n mới kh&ocirc;ng muốn l&acirc;y một &ocirc;ng x&atilde; tuyệt sắc, lại bị đ&ocirc;i tiện nam nữ h&atilde;m hại, bị người bạn th&acirc;n nhất tẩy n&atilde;o, kết cục cuối c&ugrave;ng ch&iacute;nh l&agrave; kh&ocirc;ng c&ograve;n người n&agrave;o muốn ở gần c&ocirc;.<br />\r\n<br />\r\nĐời n&agrave;y mặc cho c&aacute;c ngươi tr&acirc;u b&ograve; rắn rết trăm phương ngh&igrave;n kế, muốn c&ocirc; ly dị, nhường đi ng&ocirc;i vị phu nh&acirc;n. Ngượng ng&ugrave;ng qu&aacute; ~~, chỉ số th&ocirc;ng minh của bản tiểu thư đ&atilde; l&ecirc;n d&acirc;y rồi nh&eacute;!</p>', '93-co-vo-ngot-ngao-co-chut-bat-luong-vo-moi-bat-luong-co-chut-ngot-quan-quan-huu-yeu.jpg', 'quẫn quẫn hữu yên', 13, 1, '2021-06-22 10:43:45', '2021-06-28 04:17:53'),
(11, 1, 'mộng nam kha', 'mong-nam-kha', '<p>&ldquo;Cuộc đời như giấc mộng. Đừng như giấc Nam Kha ch&ocirc;n m&igrave;nh trong mộng ảo, để khi tỉnh dậy b&ecirc;n m&igrave;nh chỉ c&ograve;n lại nỗi đau&hellip;&rdquo;<br />\r\nMộng Nam Kha một c&acirc;u chuyện kh&ocirc;ng d&agrave;i của Ngưng Văn nhưng để lại tương đối cảm x&uacute;c cho người đọc. Như người dịch đ&atilde; giới thiệu truyện mang đậm chất triết l&yacute;, n&oacute;i về luật nh&acirc;n quả khi &ldquo;ta cướp đi một thứ g&igrave; của người kh&aacute;c, lại c&oacute; một người kh&aacute;c đang &acirc;m thầm cướp của ta&rdquo;, truyện quả thật rất đ&aacute;ng đọc (thuộc nh&oacute;m truyện c&oacute; thể đọc lại v&agrave;i lần ^^). C&acirc;u chuyện bắt đầu đ&atilde; vạch trần những g&oacute;c khuất phũ ph&agrave;ng, những hiện thực t&agrave;n nhẫn của cuộc đời lu&ocirc;n đổi trắng thay đen. Cuộc h&ocirc;n nh&acirc;n kh&ocirc;ng t&igrave;nh y&ecirc;u của n&agrave;ng Tr&igrave;nh Tố Nga &ndash; một b&ocirc;ng hoa xinh đẹp đầy gai độc v&agrave; Tạ Gia Kiều l&agrave; anh bu&ocirc;n ngọc cũng l&agrave; một kẻ cướp giấu m&aacute;u tanh sau lớp bọc hiền l&agrave;nh ngờ nghệch, mối t&igrave;nh vụng trộm của người vợ kh&ocirc;ng ngoan với tướng cướp Phương Bằng, c&ograve;n c&oacute; cả hai đứa con nhỏ vốn l&agrave; v&ocirc; tội trong o&aacute;n th&ugrave; của ba người n&agrave;y. Người bị phản bội sẽ trả th&ugrave;, nhưng Tạ Gia Kiều kh&ocirc;ng d&ugrave;ng c&aacute;ch ch&eacute;m giết, hắn thả sống con mồi, ngạo nghễ nh&igrave;n họ lăn lộn giữa &ldquo;thế gian &ocirc; trọc&rdquo;, giữa &ldquo;l&ograve;ng người đen bạc&rdquo;. Hắn chỉ việc đợi, đợi đến ng&agrave;y những viễn cảnh &aacute;i &acirc;n tươi đẹp của kẻ phụ t&igrave;nh sụp đổ, sau năm mười năm hay l&acirc;u hơn nữa liệu đoạn t&igrave;nh vụng c&oacute; c&ograve;n nồng. Bằng lương t&acirc;m của một người từng l&agrave;m cha Tạ Gia Kiều gửi gắm hai đứa trẻ cho người kh&aacute;c nu&ocirc;i nấng, chỉ mong cuộc đời ch&uacute;ng đừng rơi v&agrave;o kết cục như những đấng sinh th&agrave;nh. Rồi sau những &acirc;n o&aacute;n t&igrave;nh th&ugrave; sẽ c&oacute; kẻ trắng tay, c&oacute; người o&aacute;n hận, chung quy cũng do g&acirc;y qu&aacute; nhiều nghiệp chướng, quả b&aacute;o liệu c&oacute; ứng&hellip;?<br />\r\nNh&acirc;n quả tuần ho&agrave;n&hellip; Nhiều năm sau, đủ l&acirc;u để con người hướng thiện l&agrave;m lại cuộc đời, cũng đủ d&agrave;i khiến họ g&acirc;y ra những sai lầm trầm trọng hơn. D&ograve;ng đời x&ocirc; đẩy, l&agrave; tạo h&oacute;a tr&ecirc;u ngươi hay l&agrave; gieo &aacute;c nh&acirc;n nhận quả đắng, d&ugrave; c&oacute; l&agrave; g&igrave; quả b&aacute;o kh&ocirc;ng chọn sai người, chỉ thương cho hai đứa trẻ ng&agrave;y xưa, hai linh hồn trong trẻo ấy vốn kh&ocirc;ng n&ecirc;n phải hoen ố bụi trần. Nhưng nỗi đời l&agrave; trần trụi như vậy&hellip;&nbsp;&nbsp;<br />\r\nL&agrave; cha mẹ kh&ocirc;ng nhận ra con ruột của m&igrave;nh&hellip;<br />\r\n&ldquo;B&eacute; con năm đ&oacute; b&aacute;m s&aacute;t v&agrave;o hắn, đ&ograve;i hắn c&otilde;ng, đ&ograve;i ăn hồ l&ocirc; ng&agrave;o đường&hellip; Vậy m&agrave;&hellip;&rdquo; Đời c&oacute; bao nhi&ecirc;u lần &ldquo;vậy m&agrave;&rdquo; đ&acirc;y? Một bước sa ch&acirc;n sẽ chẳng c&ograve;n quay lại được&hellip;<br />\r\nL&agrave; người mẹ tay nhuốm m&aacute;u con&hellip;<br />\r\n&ldquo;Con g&aacute;i giờ đang nằm trong v&ograve;ng tay mẹ. Chỉ l&agrave; thay cho vẻ tươi cười linh động l&agrave; nột c&aacute;i x&aacute;c lạnh ngắt. Con kh&ocirc;ng biết, con chết ch&iacute;nh từ tay mẹ.&rdquo;<br />\r\nL&agrave; người cha tướng cướp giết người kh&ocirc;ng gớm tay giờ khắc n&agrave;y đang hoảng loạn nh&igrave;n m&aacute;u đỏ con m&igrave;nh chảy tr&agrave;n trong mắt, một đ&ecirc;m bạc đầu, một đ&ecirc;m mất đi vợ, cả hai người con, sinh ly tử biệt đến thế l&agrave; c&ugrave;ng&hellip;<br />\r\n&ldquo;Đừng bỏ cha m&agrave;&hellip; Di&ecirc;n B&igrave;nh, Di&ecirc;n Ch&acirc;u&hellip;&rdquo; Gọi cho ai nghe đ&acirc;y? Khi người đ&atilde; xa vĩnh viễn&hellip; C&oacute; hối kh&ocirc;ng? C&oacute; đau kh&ocirc;ng?<br />\r\nL&agrave; người anh b&agrave;ng ho&agrave;ng với nỗi đau loạn lu&acirc;n d&agrave;y x&eacute; đến tận cửu tuyền.<br />\r\n&ldquo;Tại sao, tại sao t&ocirc;i lại l&agrave; Di&ecirc;n B&igrave;nh chứ? Tại sao&hellip;?&rdquo;<br />\r\n&ldquo;Cũng&hellip; Cũng tốt&hellip; N&agrave;ng&hellip; Kh&ocirc;ng biết g&igrave; cả&hellip; N&agrave;ng chỉ ngủ&hellip; Kh&ocirc;ng cần phải biết. D&ugrave; thế n&agrave;o&hellip; Cũng chỉ m&igrave;nh ta biết&hellip; Nương tử&hellip; Tiểu muội&hellip; Cứ để cho ta&hellip;&rdquo;&nbsp;<br />\r\nL&agrave; vợ cũng l&agrave; em g&aacute;i, l&agrave; chồng lại cũng l&agrave; anh trai, đau n&agrave;o như đau n&agrave;y, người t&ecirc;n Di&ecirc;n B&igrave;nh ấy chọn cho m&igrave;nh c&aacute;i chết, lối giải tho&aacute;t đ&oacute; đem đến một kết đoạn buồn nhưng l&agrave; hợp l&yacute;.<br />\r\nMột người anh trai mong mỏi &ldquo;d&ugrave; l&agrave; khi c&ograve;n sống hay khi đ&atilde; mất, n&agrave;ng vẫn lu&ocirc;n được y&ecirc;u thương che chở. N&agrave;ng sẽ l&agrave; một linh hồn thanh khiết. C&ograve;n ta, mọi tội lỗi, mọi lời nguyền rủa, loạn lu&acirc;n, h&atilde;y để một m&igrave;nh ta chịu, c&oacute; được kh&ocirc;ng?&rdquo; Mong l&agrave; được&hellip;&nbsp; &nbsp;<br />\r\nB&ecirc;n lề của nỗi đau một người chồng hy vọng &ldquo;kiếp sau được Di&ecirc;m Vương thương x&oacute;t, cho đầu thai tiếp tục duy&ecirc;n vợ chồng&rdquo;. Mong ch&agrave;ng được như &yacute; nguyện&hellip;<br />\r\nC&oacute; một nỗi x&oacute;t xa nh&egrave; nhẹ chậm r&atilde;i d&acirc;ng l&ecirc;n cay nồng kh&oacute;e mắt. Khổ sở qu&aacute;, d&agrave;y v&ograve; qu&aacute;&hellip; Đ&uacute;ng l&agrave; SE trong truyền thuyết&hellip;<br />\r\nC&ograve;n một đoạn cuối, khi cố nh&acirc;n gặp lại nay người y&ecirc;n ổn kẻ suy t&agrave;n. Phương Bằng th&acirc;n t&agrave;n ma dại, cửa n&aacute;t nh&agrave; tan sống dật dờ như một c&aacute;i b&oacute;ng đối diện với t&igrave;nh địch ng&agrave;y xưa &ndash; Tạ Gia Kiều giờ đ&atilde; l&agrave; một ph&uacute; thương gi&agrave;u c&oacute;, n&eacute;t thanh thản in hằn l&ecirc;n cốt c&aacute;ch. Tr&ocirc;ng người nh&igrave;n m&igrave;nh, tr&aacute;ch trời cao bất c&ocirc;ng sao kh&ocirc;ng ngẫm lại m&igrave;nh đ&acirc;y&hellip;<br />\r\n&ldquo;V&igrave; ta biết dừng lại&hellip; C&ograve;n ngươi th&igrave; kh&ocirc;ng...&rdquo;<br />\r\nBao nhi&ecirc;u đ&oacute; đ&atilde; đủ giải th&iacute;ch chưa? Hai người đ&agrave;n &ocirc;ng, kẻ bị tham vọng hủy hoại, người y&ecirc;n phận kh&ocirc;ng tranh chấp với qu&aacute; khứ. Đằng sau nỗi đau của Phương Bằng l&agrave; một tấn bi kịch, l&agrave; nuối tiếc kh&ocirc;n ngu&ocirc;i, c&ograve;n của Tạ gia Kiều l&agrave; hạnh ph&uacute;c, l&agrave; an ổn của kẻ thức thời. Đến cuối c&ugrave;ng l&agrave; đ&uacute;ng, l&agrave; sai, l&agrave; nghiệp b&aacute;o, đều như mộng tại đời thường.<br />\r\nC&aacute;i kết với những lời văn đầy nh&acirc;n văn, khiến cho ta kh&ocirc;ng chỉ đọc su&ocirc;ng m&agrave; c&ograve;n phải ngẫm.<br />\r\nCảm ơn t&aacute;c giả Ngưng Văn đ&atilde; viết n&ecirc;n Mộng Nam Kha.<br />\r\nCảm ơn Greenhousenovels, nh&oacute;m dịch muadongxaxam &ndash; lvd &ndash; vtmk đ&atilde; mang Mộng Nam Kha đến với độc giả.</p>', '39-mong-nam-kha.jpg', 'ngưng văn', 3, 1, '2021-06-22 10:46:49', '2021-06-28 03:12:24'),
(12, 1, 'lấy chồng bạc tỷ', 'lay-chong-bac-ty', '<p>Cuộc sống lu&ocirc;n c&oacute; những điều bất ngờ xảy ra v&iacute; dụ như Giang Nhung mong muốn c&oacute; cuốc sống b&igrave;nh thường n&ecirc;n đồng &yacute;&nbsp; gả cho một người đ&agrave;n &ocirc;ng b&igrave;nh thường. Nhưng người đ&agrave;n &ocirc;ng b&igrave;nh thường ấy lại trở th&agrave;nh Sếp c&ocirc;ng ty c&ocirc;.</p>\r\n\r\n<p>Kh&ocirc;ng dừng lại ở đ&oacute; m&agrave;, anh ta c&ograve;n l&agrave; người thừa kế của Tập đo&agrave;n Đế Quốc gi&agrave;u nhất Ch&acirc;u &Aacute;. B&ecirc;n ngo&agrave;i, l&agrave; một người nắm quyền kinh doanh quyết đo&aacute;n, m&aacute;u lạnh v&ocirc; t&igrave;nh. M&agrave; b&ecirc;n trong, Anh ta lại l&agrave; một t&ecirc;n s&oacute;i đội lốt cừu, &ldquo;gặm&rdquo; n&agrave;ng đến xương cốt cũng kh&ocirc;ng chừa.</p>', '39-lay-chong-bac-ty.jpg', 'mộng thất thất', 10, 1, '2021-06-22 10:49:47', '2021-06-28 03:15:22'),
(13, 1, 'boss cuồng yêu vợ', 'boss-cuong-yeu-vo', '<p>L&uacute;c n&agrave;y c&ocirc; mới nh&igrave;n xuống gương mặt b&eacute; bỏng của Giai Kỳ, b&eacute; g&aacute;i nhỏ trong v&ograve;ng tay c&ocirc; v&ocirc; c&ugrave;ng đ&aacute;ng y&ecirc;u. T&iacute;nh ra c&ocirc; đ&atilde; sinh tiểu c&ocirc;ng ch&uacute;a n&agrave;y được 3 th&aacute;ng rồi. Thời gian quả l&agrave; tr&ocirc;i mau! Mới ng&agrave;y n&agrave;o c&ocirc; c&ograve;n đang vui vẻ khi biết m&igrave;nh c&oacute; mang thai.</p>\r\n\r\n<p>Thấy Diệp Tử &Aacute;i dần ngu&ocirc;i ngoai cơn giận anh mới chậm r&atilde;i tiến về ph&iacute;a c&ocirc; &ocirc;m lấy cả người c&ocirc; v&agrave;o l&ograve;ng m&agrave; thủ thỉ.</p>\r\n\r\n<p>&quot;Giai Kỳ ngủ rất ngoan!&quot;</p>\r\n\r\n<p>&quot;Phải...mới đ&oacute; th&ocirc;i m&agrave; em cứ ngỡ như h&ocirc;m qua! Ng&ocirc;n c&oacute; phải b&acirc;y giờ tr&ocirc;ng em rất giống mấy b&agrave; mẹ bỉm sữa kh&ocirc;ng?&quot;</p>\r\n\r\n<p>Bạch Tử Ng&ocirc;n vuốt nhẹ ngọn t&oacute;c của c&ocirc; sau đ&oacute; khẽ lắc đầu cười.</p>\r\n\r\n<p>&quot;Kh&ocirc;ng giống! Em vẫn xinh đẹp như vậy chẳng thay đổi ch&uacute;t n&agrave;o&quot;</p>\r\n\r\n<p>&quot;Anh n&oacute;i dối!&quot; Diệp Tử &Aacute;i bỗng nhi&ecirc;n cảm thấy c&oacute; ch&uacute;t tủi th&acirc;n c&uacute;i mặt xuống</p>\r\n\r\n<p>Ng&oacute;n tay khẽ n&acirc;ng gương mặt c&ocirc; l&ecirc;n sau đ&oacute; h&ocirc;n l&ecirc;n tr&aacute;n c&ocirc; một c&aacute;ch &ocirc;n nhu nhất c&oacute; thể.</p>\r\n\r\n<p>&quot;D&ugrave; c&oacute; như thế n&agrave;o đi nữa th&igrave; em vẫn l&agrave; vợ của Bạch Tử Ng&ocirc;n n&agrave;y! Anh sẽ kh&ocirc;ng hết y&ecirc;u miếng n&agrave;o đ&acirc;u n&ecirc;n đừng c&oacute; tự nghĩ ngợi nhiều nữa c&oacute; biết chưa? Anh biết qu&atilde;ng thời gian n&agrave;y l&agrave; rất kh&oacute; khăn với em nhưng em vẫn c&ograve;n c&oacute; anh b&ecirc;n cạnh m&agrave; chẳng phải sao? Em xem Giai Kỳ của ch&uacute;ng ta cũng đ&aacute;ng y&ecirc;u xinh đẹp giống mẹ n&oacute; vậy!&quot;<br />\r\nDiệp Tử &Aacute;i nh&igrave;n trong đ&ocirc;i mắt anh tia ấm &aacute;p l&oacute;e l&ecirc;n trong l&ograve;ng c&ocirc; một c&aacute;ch nhẹ nh&agrave;ng.</p>\r\n\r\n<p>&quot;Ba mẹ...&quot; Tiểu H&agrave;nh vừa đi học về đ&atilde; vội v&agrave;ng chạy đến chỗ hai người.</p>\r\n\r\n<p>&quot;Tiểu H&agrave;nh lại đ&acirc;y với ba&quot; Bạch Tử Ng&ocirc;n bế cậu nh&oacute;c nhỏ l&ecirc;n</p>', '95-boss-cuong-vo-yeu.jpg', 'lễu mộc mộc', 13, 1, '2021-06-22 10:53:01', '2021-06-28 03:17:38'),
(14, 1, 'sếp chúng tôi lại khoe vợ', 'sep-chung-toi-lai-khoe-vo', '<p>T&aacute;c phẩm: Sếp Ch&uacute;ng T&ocirc;i Lại Khoe Vợ.<br />\r\nThể loại: Chủ c&ocirc;ng, hỗ sủng, ngọt văn, thụ tr&ugrave;ng sinh, cường cường, t&igrave;nh hữu độc chung, huyền nghi, trinh th&aacute;m, 1x1, HE.&nbsp;&nbsp;<br />\r\nT&aacute;c giả: Họa Ngữ.<br />\r\nEdit: OoGui B&ocirc;ng.<br />\r\nLắm lời, lười biếng, ph&uacute; nhị đại, trung khuyển c&ocirc;ng.&nbsp;&nbsp;<br />\r\nThịnh thế mỹ nh&acirc;n, c&aacute;n bộ kỳ cựu, tr&ugrave;ng sinh thụ.&nbsp;<br />\r\nNh&acirc;n vật ch&iacute;nh: Giản Ng&ocirc;n, A Từ.&nbsp;&nbsp;<br />\r\nĐộ dài: 92c + 2pn</p>', '27-sep-chung-toi-lai-khoe-vo.jpg', 'hòa ngư', 17, 1, '2021-06-22 10:53:41', '2021-06-28 07:41:46'),
(15, 1, 'văn phòng phấn hồng', 'van-phong-phan-hong', '<p>Bạn c&oacute; hạnh ph&uacute;c kh&ocirc;ng? Bạn trai, bạn g&aacute;i, vợ chồng c&oacute; chung thủy với bạn kh&ocirc;ng? Hạnh ph&uacute;c bị cướp mất, niềm vui bị mất đi. Người tưởng l&agrave; y&ecirc;u m&igrave;nh cuối c&ugrave;ng lại biến th&agrave;nh tra nam tiện nữ.</p>\r\n\r\n<p>Bạn muốn lấy nước mắt rửa mặt, nhịn nhục nh&igrave;n ch&uacute;ng b&ecirc;n nhau, hay bạn chọn trừng phạt kẻ bội t&igrave;nh? Nếu bạn căm hận khổ đau, nếu bạn kh&ocirc;ng cam l&ograve;ng, nếu bạn kh&ocirc;ng muốn kẻ khốn nạn cười ng&ocirc;ng ngh&ecirc;nh&hellip;</p>\r\n\r\n<p>𝘝𝘢̣̂𝘺 𝘩𝘰𝘢𝘯 𝘯𝘨𝘩𝘦̂𝘯𝘩 𝘣𝘢̣𝘯 đ𝘦̂́𝘯 𝘝𝘢̆𝘯 𝘱𝘩𝘰̀𝘯𝘨 𝘗𝘩𝘢̂́𝘯 𝘏𝘰̂̀𝘯𝘨!!!</p>\r\n\r\n<p>Nữ ch&iacute;nh Phương Hiểu Hiểu mở một văn ph&ograve;ng đặt t&ecirc;n Phấn Hồng, chuy&ecirc;n nhận c&aacute;c phi vụ trả th&ugrave; t&igrave;nh cũ, do người bị phụ bạc ủy th&aacute;c.</p>\r\n\r\n<p>C&ocirc; c&oacute; phương ph&aacute;p dạy dỗ đ&aacute;m tra nam tiện nữ, tiểu tam của ri&ecirc;ng m&igrave;nh, xua tan nỗi đau trong l&ograve;ng người bị phản bội. C&ocirc; trừng phạt những kẻ ngoại t&igrave;nh, để bọn họ nhận ra sai lầm của m&igrave;nh, hối cải, biết quay đầu l&agrave; bờ!</p>\r\n\r\n<p>Nam ch&iacute;nh mời qu&yacute; vị đọc truyện để biết th&ecirc;m chi tiết :v</p>', '96-van-phong-phan-hong.jpg', 'ly hoa', 5, 1, '2021-06-22 10:57:13', '2021-06-28 07:06:53'),
(16, 1, 'người tình trí mạng', 'nguoi-tinh-tri-mang', '<p>Tr&ecirc;n đời n&agrave;y c&oacute; một thứ.</p>\r\n\r\n<p>Kh&ocirc;ng nghe thấy tiếng, kh&ocirc;ng nh&igrave;n thấy h&igrave;nh, nhưng lại tồn tại ở khắp mọi nơi tr&ecirc;n trời đất n&agrave;y.</p>\r\n\r\n<p>M&ugrave;i hương.</p>\r\n\r\n<p>N&oacute; c&oacute; thể chữa bệnh cho người trong y&ecirc;n lặng, lại c&oacute; thể giết chết người ta trong v&ocirc; h&igrave;nh.</p>\r\n\r\n<p>N&oacute; ẩn nấp dưới từng lớp da thịt, trở th&agrave;nh hơi thở của bạn, khiến cho b&iacute; mật của bạn kh&ocirc;ng c&oacute; chỗ trốn.</p>\r\n\r\n<p>C&ocirc; l&agrave; một nh&agrave; tạo hương, v&igrave; c&oacute; được một khứu gi&aacute;c trời sinh nhạy cảm, được mệnh danh l&agrave; bậc thầy hương thơm hiếm c&oacute; tr&ecirc;n đời, thế nhưng v&agrave;o l&uacute;c danh tiếng lẫy lừng nhất, c&ocirc; bỗng bất ngờ mai danh ẩn t&iacute;ch&hellip;</p>\r\n\r\n<p>Anh l&agrave; một doanh nh&acirc;n của Lục M&ocirc;n, v&igrave; những h&agrave;nh động quyết đo&aacute;n v&agrave; những mưu kế kh&ocirc;n lường, được mệnh danh l&agrave; chiến thần của giới thương nh&acirc;n, nhưng ngay v&agrave;o l&uacute;c chuẩn bị ngồi l&ecirc;n đỉnh cao quyền lực th&igrave; bỗng rơi v&agrave;o v&ograve;ng khốn đốn, t&iacute;nh mạng ng&agrave;n c&acirc;n treo sợi t&oacute;c&hellip;</p>\r\n\r\n<p>C&ocirc; rong ruổi tr&ecirc;n đại mạc, ẩn m&igrave;nh chốn cao nguy&ecirc;n.</p>\r\n\r\n<p>Anh tung ho&agrave;nh tr&ecirc;n thương trường, th&acirc;m nhập v&agrave;o nơi tận c&ugrave;ng của nh&acirc;n t&iacute;nh.</p>\r\n\r\n<p>Gặp gỡ, tất cả đều kh&ocirc;ng phải l&agrave; ngẫu nhi&ecirc;n.</p>\r\n\r\n<p>Một b&iacute; k&iacute;p chế tạo m&ugrave;i hương đ&atilde; thất truyền ng&agrave;n năm bất ngờ t&aacute;i xuất giang hồ, tấm bản đồ ẩn chứa t&agrave; kh&iacute;&hellip;</p>\r\n\r\n<p>Một cổ th&agrave;nh ng&agrave;n năm ẩn m&igrave;nh dưới nước s&acirc;u, một quan t&agrave;i thần b&iacute; tr&ocirc;i nổi trong hang động.</p>\r\n\r\n<p>Gỗ tương tư của đ&ocirc;i trai g&aacute;i si t&igrave;nh, một c&ocirc; đ&agrave;o k&eacute;p thật thật giả giả&hellip;</p>\r\n\r\n<p>Tr&ugrave;ng tr&ugrave;ng lớp lớp những sự kiện ly kỳ tr&ocirc;ng c&oacute; vẻ chẳng hề d&iacute;nh l&iacute;u tới nhau,</p>\r\n\r\n<p>kỳ thực đều gắn chặt với m&ugrave;i hương.</p>\r\n\r\n<p>Người kh&ocirc;ng thể trốn tr&aacute;nh m&ugrave;i hương, thế n&ecirc;n, t&ocirc;i c&oacute; thể kiểm so&aacute;t người trong thinh lặng.</p>\r\n\r\n<p>T&aacute;c phẩm tiểu thuyết t&igrave;nh y&ecirc;u lần đầu đề cập s&acirc;u tới &ldquo;m&ugrave;i hương&rdquo;, cảm nhận một cảnh giới học thuật về m&ugrave;i.</p>', '9-nguoi-tinh-tri-mang-an-tam.jpg', 'ân tầm', 4, 1, '2021-06-22 10:58:56', '2021-06-28 03:22:28'),
(17, 1, 'y lộ phong hoa', 'y-lo-phong-hoa', '<p>C&ocirc; l&agrave; b&aacute;c sĩ ở hiện đại, kh&ocirc;ng hiểu tại sao lại xuy&ecirc;n kh&ocirc;ng đến vương triều Đại Tề trở th&agrave;nh tiểu ngốc tử trong phủ.<br />\r\n<br />\r\nKh&ocirc;ng thể sinh con trai, mẫu th&acirc;n bị phụ th&acirc;n hắt hủi, lại c&ograve;n nằm tr&ecirc;n giường bệnh triền mi&ecirc;n, từ đ&oacute; tiểu thiếp nắm quyền, n&agrave;ng lưu lạc bị người đời ch&ecirc; cười.<br />\r\n<br />\r\nMẹ ruột yếu đuối, tổ mẫu cay nghiệt, phụ th&acirc;n kh&ocirc;ng th&iacute;ch, c&ugrave;ng nhị nương ch&aacute;n gh&eacute;t, thứ muội ngoan độc. Trong mắt mọi người Tiết V&ocirc; Ưu l&agrave; người so với ngốc tử kh&ocirc;ng kh&aacute;c biệt lắm. C&ocirc; xuy&ecirc;n qua trở th&agrave;nh &ldquo;n&agrave;ng&rdquo;, che giấu t&agrave;i năng, nghiễm kết thiện duy&ecirc;n*, mở tiệm thuốc, mua n&ocirc;ng trang, bố tr&iacute; điền x&aacute;, đấu với nhị nương, trừng trị thứ muội, kết giao quyền qu&yacute;... Từ đ&oacute;, mẫu th&acirc;n gi&agrave;nh được sự sủng &aacute;i, n&agrave;ng trở th&agrave;nh bảo bối trong tay tổ mẫu phụ th&acirc;n, c&ocirc;ng tử tướng phủ, đại tướng qu&acirc;n uy vũ lần lượt b&agrave;y tỏ t&igrave;nh y&ecirc;u, thật sự l&agrave; ngoạn chuyển cổ đại!<br />\r\n<br />\r\n[*nghiễm kết thiện duy&ecirc;n: kế hoạch hợp t&aacute;c quan hệ c&oacute; lợi cho đ&ocirc;i b&ecirc;n]<br />\r\n<br />\r\n[*ngoạn chuyển cổ đại: chỉ sự việc, l&ograve;ng người mau thay đổi]<br />\r\n<br />\r\nHắn, d&ograve;ng trưởng phủ thừa tướng, con của c&ocirc;ng ch&uacute;a, b&aacute;i danh Đại L&yacute;, h&igrave;nh tượng ti&ecirc;u s&aacute;i nho nh&atilde;, th&uacute; vị lại h&agrave;i hước, nhưng rất quan t&acirc;m, &ocirc;n nhu săn s&oacute;c n&agrave;ng.<br />\r\n<br />\r\nHắn, nhị c&ocirc;ng tử phủ Hầu gia, cữu gia đương triều, đại tướng qu&acirc;n uy vũ, oai h&ugrave;ng lại ki&ecirc;n nghị, hiệp cốt nhu tr&agrave;ng*, đối n&agrave;ng chuy&ecirc;n nhất t&igrave;nh, ngoan ngo&atilde;n phục t&ugrave;ng. Oa oa oa... Muốn n&agrave;ng chọn người n&agrave;o a?<br />\r\n<br />\r\nThật sự l&agrave;m Tiết V&ocirc; Ưu đau đầu gần chết!<br />\r\n<br />\r\n[*hiệp cốt nhu tr&agrave;ng: &yacute; chỉ người anh h&ugrave;ng bề ngo&agrave;i th&ocirc; lỗ nhưng c&oacute; tấm l&ograve;ng &ocirc;n nhu, đối xử tốt bụng với mọi người, đặc biệt l&agrave; phụ nữ]<br />\r\n<br />\r\n=== ======&nbsp;<br />\r\n<em>T&ecirc;n quyển s&aacute;ch: Văn chuy&ecirc;n t&igrave;nh đ&iacute;ch nữ th&iacute;ch tướng qu&acirc;n dốc l&ograve;ng l&agrave;m ruộng.</em></p>', '92-y-lo-phong-hoa-linh-khe.jpg', 'linh khuê', 6, 1, '2021-06-22 11:00:29', '2021-06-28 07:07:17'),
(18, 1, 'bổ thiên ký', 'bo-thien-ky', '<p>C&ograve;n đường tu h&agrave;nh chưa bao giờ l&agrave; đơn giản, kh&ocirc;ng những cần c&oacute; những tố chất &quot;kh&ocirc;ng thể thiếu&quot; như c&ocirc;ng ph&aacute;p, tố chất, cơ duy&ecirc;n, linh căn m&agrave; c&ograve;n phải chịu sự thử th&aacute;ch kh&ocirc;ng k&eacute;m phần nguy hiểm từ việc nghịch tập của c&aacute;c nữ phụ xuy&ecirc;n kh&ocirc;ng, trọng sinh từng bị l&agrave;m bia đỡ đạn.</p>\r\n\r\n<p>Đủ loại &aacute;p lực khiến nữ ch&iacute;nh Vinh Tuệ Khanh cảm thấy bị dồn &eacute;p...</p>\r\n\r\n<p>L&atilde;o thi&ecirc;n chết liệt, tại sao nhiều lỗ hổng như vậy chứ!!!</p>\r\n\r\n<p>&quot;V&aacute; trời&quot; kh&ocirc;ng tốn tiền hả???</p>', '96-bo-thien-ky.jpg', 'hàn vũ ký', 13, 1, '2021-06-22 11:01:00', '2021-06-28 07:06:38'),
(19, 1, 'ninh tiểu nhàn, ngự thần lục', 'ninh-tieu-nhan-ngu-than-luc', '<p>Đ&aacute;m nh&oacute;c từng ng&agrave;y lớn l&ecirc;n, đối với c&aacute;i g&igrave; cũng t&ograve; m&ograve;, cho n&ecirc;n hỏi mẫu th&acirc;n &ldquo;Tại sao lại gả cho phụ th&acirc;n?&rdquo;</p>\r\n\r\n<p>Ninh&nbsp;Tiểu Nh&agrave;n: &ldquo;Nhớ năm đ&oacute; phụ th&acirc;n c&aacute;c con&nbsp;bị giam&nbsp;hơn ba vạn năm, ngồi đến sắp chai m&ocirc;ng. Mẫu th&acirc;n tội nghiệp hắn, cho n&ecirc;n trải qua nhiều trăm cay ngh&igrave;n đắng, đi trăm vạn dặm h&agrave;nh tr&igrave;nh, một đường vượt qua mọi ch&ocirc;ng gai, trảm y&ecirc;u trừ ma, giết người đoạt bảo, mưu t&agrave;i hại &nbsp;mệnh. . . . . . &Aacute;, d&ugrave; sao cuối c&ugrave;ng liều chết đ&atilde;&nbsp;cứu được hắn ra. Phụ th&acirc;n c&aacute;c con cảm động đến rơi nước mắt, cho n&ecirc;n lấy th&acirc;n b&aacute;o đ&aacute;p! Đ&atilde; hiểu chưa?&rdquo;</p>\r\n\r\n<p>Đ&aacute;m nh&oacute;c trăm miệng một lời: &ldquo;Đ&atilde; hiểu, ho&aacute; ra&nbsp;l&agrave; cha gả cho mẹ&rdquo;</p>\r\n\r\n<p>Ninh&nbsp;Tiểu Nh&agrave;n: &ldquo;╮(￣▽￣)╭ &nbsp;Bảo Nhi của ta thật&nbsp;l&agrave; th&ocirc;ng minh.&rdquo;</p>\r\n\r\n<p>B&ecirc;n cạnh đột nhi&ecirc;n vang l&ecirc;n giọng n&oacute;i lạnh như băng : &ldquo;Mới vừa rồi, n&agrave;ng n&oacute;i ai lấy th&acirc;n b&aacute;o đ&aacute;p?&rdquo;</p>\r\n\r\n<p>Ninh&nbsp;Tiểu Nh&agrave;n: &ldquo;Ta, ta, đương nhi&ecirc;n l&agrave; ta! Khụ, l&agrave;m sao ch&agrave;ng xuất quan sớm vậy?&rdquo;</p>\r\n\r\n<p>Được rồi, đ&acirc;y l&agrave; một c&acirc;u chuyện giải cứu v&agrave;&nbsp;bị giải cứu, tấn c&ocirc;ng v&agrave; bị tấn c&ocirc;ng, mưu t&iacute;nh c&ugrave;ng phản mưu t&iacute;nh.</p>', '14-ninh-tieu-nhan-ngu-than-luc-phong-hanh-thuy-van-gian.jpg', 'phong hành thủy vân gian', 25, 1, '2021-06-22 11:02:19', '2021-06-28 07:05:58'),
(23, 2, 'đao giả bá vương', 'dao-gia-ba-vuong', '<p>Từ chốn giang hồ đ&atilde; lưu truyền về T&agrave; Vương&nbsp;Thần Qu&acirc;n Thi&ecirc;n l&agrave; thi&ecirc;n&nbsp;hạ duy ng&atilde; độc t&ocirc;n. Một chiến thần&nbsp;xưng danh to&agrave;n c&otilde;i giang hồ v&otilde; l&acirc;m chỉ với một m&igrave;nh hắn.<br />\r\n<br />\r\nCũng bởi lẽ hắn c&oacute; thể th&ocirc;ng thạo đủ mọi tuyệt kỹ đao, quyền, kiếm, cước, chưởng, độc, y thuật. Mạnh th&igrave; sẽ sinh ra ki&ecirc;u ngạo, hắn t&agrave;n s&aacute;t v&ocirc; số người kh&ocirc;ng thể đếm được.<br />\r\n<br />\r\nTuy nhi&ecirc;n T&agrave; Vương cũng c&oacute; người d&aacute;m đối đầu với hắn l&agrave; Thần V&otilde; Anh Kiệt.<br />\r\n<br />\r\nNgười đồn rằng trận chiến giữa hai người trải qua suốt hai tuần lễ, kh&ocirc;ng ngừng nghỉ nhưng diễn biến th&igrave; kh&ocirc;ng ai r&otilde;. Chỉ biết l&agrave; sau trận đ&aacute;nh&nbsp;th&igrave; T&agrave; Vương Thần Qu&acirc;n v&agrave; Thần V&otilde; Anh Kiệt biến mất kh&ocirc;ng dấu t&iacute;ch. C&oacute; người n&oacute;i rằng cả hai tung ra b&iacute; kỹ của m&igrave;nh dẫn đến tự bạo v&agrave;&nbsp;từ đ&oacute;&nbsp;đủ mọi c&acirc;u chuyện thu&ecirc; dệt.<br />\r\n<br />\r\n100 năm sau,...một T&agrave; Vương xuất thế liệu Thần V&otilde; cũng như thế chăng?Giang hồ sẽ lại c&oacute; một phen s&oacute;ng gi&oacute;?<br />\r\n<br />\r\n----------------------------------------------<br />\r\n<br />\r\nCảnh Giới Cường Giả<br />\r\n<br />\r\nSi&ecirc;u Ph&agrave;m Nhập Th&aacute;nh: Cường giả c&oacute; ngộ t&iacute;nh cao, d&ugrave; l&agrave; bất cứ loại v&otilde; học g&igrave; cũng nhanh ch&oacute;ng luyện th&agrave;nh<br />\r\n<br />\r\nSi&ecirc;u Th&aacute;nh Nhập Thần: C&oacute; thể dung nạp v&otilde; học tiền nh&acirc;n v&agrave;o tư duy nhất, cải tiến, đột ph&aacute; cảnh giới v&otilde; học<br />\r\n<br />\r\nSi&ecirc;u Thần Nhập H&oacute;a: C&oacute; thể dựa v&agrave;o trực gi&aacute;c, &yacute; niệm m&agrave; l&uacute;c l&acirc;m trận tự c&oacute; thể s&aacute;ng chế ra chi&ecirc;u thức<br />\r\n<br />\r\nSi&ecirc;u H&oacute;a Ho&agrave;n Hư: Cảnh giới trong truyền thuyết. C&aacute;c cường giả ở cảnh giới n&agrave;y c&oacute; thể tr&ugrave;ng quy hư v&ocirc;, cải tử ho&agrave;n sinh(chưa ai ngo&agrave;i Đạt Ma sư tổ Thiếu L&acirc;m v&agrave; Vương Tr&ugrave;ng Dương sư tổ To&agrave;n Ch&acirc;n)<br />\r\n<br />\r\n&Agrave; m&agrave; đ&acirc;y l&agrave; truyện kiếm hiệp chứ kh&ocirc;ng phải ti&ecirc;n hiệp. Đừng nhầm v&igrave; sao l&ecirc;n cấp nhanh</p>', '51-dao-gia-ba-vuong.jpg', 'tiêu dao kk', 3, 1, '2021-06-28 03:36:30', '2021-06-28 03:59:06'),
(24, 2, 'hắc long', 'hac-long', '<p>Muốn biết nội dung truyện mời c&aacute;c ban đọc, truyện n&agrave;y t&aacute;c giả viết theo thể loại sắc n&ecirc;n sẽ c&oacute; những cảnh nghi&ecirc;m cấm trẻ em xem nha,&nbsp;chưa đủ tuổi đừng đọc kẻo ta k trả lại mắt được cho c&aacute;c vị đ&acirc;u hehe:))))</p>', '90-hac-long.jpg', 'nhatha255', 1, 1, '2021-06-28 03:37:33', '2021-06-28 03:59:21'),
(25, 2, 'hủy diệt hệ thống', 'huy-diet-he-thong', '<p>Truyện l&agrave; t&aacute;c phẩm đầu tay của t&aacute;c giả, chỗ n&agrave;o thiếu s&oacute;t người đọc g&oacute;p &yacute; cho t&aacute;c giả.<br />\r\n<br />\r\nThanh Phong tỏ t&igrave;nh với Linh th&igrave; lại bị từ chối khiến mọi thứ như sụp đổ trong hắn.<br />\r\n<br />\r\nHắn chạy xe tr&ecirc;n đường như x&aacute;c kh&ocirc;ng hồn khiến xe lao thằng v&agrave;o xe tải... rầm.<br />\r\nHắn tỉnh lại v&agrave; nh&igrave;n thấy bản th&acirc;n đang tr&ocirc;i nổi trong khoảng kh&ocirc;ng v&ocirc; tận. Hắn tự hỏi:<br />\r\n<br />\r\n- M&igrave;nh đang ở đ&acirc;u, m&igrave;nh đ&atilde; chết rồi sao.<br />\r\n<br />\r\nC&oacute; giọng n&oacute;i vang l&ecirc;n:<br />\r\n<br />\r\n- Xin ch&agrave;o chủ nh&acirc;n, ch&agrave;o mừng chủ nh&acirc;n đ&atilde; tỉnh lại.</p>', '15-huy-diet-he-thong.jpg', 'thianhnam000', 0, 1, '2021-06-28 03:38:11', '2021-06-28 03:38:11'),
(26, 2, 'đường trở về tại vị thế giới', 'duong-tro-ve-tai-vi-the-gioi', '<p><br />\r\nNh&acirc;n vật ch&iacute;nh của ta bị bệnh m&agrave; chết, kh&ocirc;ng hiểu sao lại trọng sinh đến dị giới. Ở nơi n&agrave;y hắn nhớ lại cuộc đời m&igrave;nh, hối hận về nh&acirc;n sinh kiếp trước, hắn ao ước c&oacute; thể trở về nơi ch&ocirc;n nhau cắt rốn, nơi m&igrave;nh được sinh ra v&agrave; lớn l&ecirc;n - Địa Cầu. Niềm ao ước muốn trở lại của hắn mạnh mẽ đến nổi n&oacute; trở th&agrave;nh động lực mạnh mẽ nhất của hắn, san bằng tất cả những chướng ngại, gặp thần giết thần, gặp phật giết phận, rung chuyển dị giới.</p>', '19-duong-tro-ve-tai-di-the-gioi.jpg', 'khoaivn9x', 0, 1, '2021-06-28 03:39:00', '2021-06-28 04:07:30'),
(27, 2, 'giang hồ tham án truyền kỳ', 'giang-ho-tham-an-truyen-ky', '<p>Giang hồ nơi n&agrave;o c&oacute; b&iacute; ẩn, đầy rẫy thị phi đều c&oacute; dấu chấn của hắn.<br />\r\n<br />\r\nT&ocirc; Chuyết chỉ mới gia nhập v&agrave;o giới giang hồ lại d&ugrave;ng sự cơ tr&iacute; của bản th&acirc;n để đưa ra lời giải đ&aacute;p cho hết b&iacute; mật n&agrave;y đến b&iacute; mật kh&aacute;c. Nhưng người chốn giang hồ th&acirc;n bất do kỷ n&ecirc;n hắn đ&atilde; bị cuốn v&agrave;o trong &acirc;m mưu của Nhạc Dương Vương thần b&iacute;.<br />\r\n<br />\r\nHắn kh&ocirc;ng tiếc hi sinh cả bản th&acirc;n để vật ng&atilde; đối thủ n&agrave;y nhưng sau đ&oacute; hắn liền ph&aacute;t hiện ra rằng, Nhạc Dương Vương chỉ l&agrave; một g&oacute;c băng nổi, &acirc;m mưu ở ph&iacute;a sau vẫn c&ograve;n ch&igrave;m s&acirc;u dưới mặt nước.<br />\r\n<br />\r\n<em>(Nhắc nhở: Mỗi một quyển l&agrave; một c&acirc;u chuyện trinh th&aacute;m xưa, bởi v&igrave; hồi hộp qu&aacute; nhiều, bằng hữu ch&aacute;y n&atilde;o chịu kh&ocirc;ng được m&ocirc;̣t chương tiếp m&ocirc;̣t chương, vậy th&igrave; phủ bụi đi, chờ đợi khi mỗi một quyển ho&agrave;n tất lại xem tiếp!)</em></p>', '57-giang-ho-tham-an-truyen-ky.jpg', 'chu tiểu xuyên', 1, 1, '2021-06-28 03:39:44', '2021-06-28 04:13:32'),
(28, 2, 'thanh niên óc chó', 'thanh-nien-oc-cho', '<p>Nội dung của truyện l&agrave; kể về những t&igrave;nh tiết hư cấu của một thế giới hư cấu, mọi tổ chức, nh&acirc;n vật, t&ocirc;n gi&aacute;o, lịch sử trong truyện đều l&agrave; hư cấu.<br />\r\n<br />\r\nV&agrave;o năm 14 tuổi th&igrave; Sơn đ&atilde;&nbsp;&nbsp;th&iacute;ch&nbsp;xem hentai v&agrave; JAV, bị kẻ trộm lỡ tay cắt tiết&nbsp;... Xuy&ecirc;n kh&ocirc;ng nhập thể v&agrave;o thiếu ni&ecirc;n gần như&nbsp;&nbsp;phế vật 16&nbsp;tuổi c&ugrave;ng t&ecirc;n. V&igrave; v&agrave;i l&yacute; do Sơn phải chứng tỏ sức mạnh của bản th&acirc;n, h&atilde;y c&ugrave;ng Sơn bước v&agrave;o thế giới tu ti&ecirc;n đầy rẫy chết ch&oacute;c.<br />\r\n<br />\r\nMời đọc Thanh Ni&ecirc;n &Oacute;c Ch&oacute;&nbsp;<br />\r\n<br />\r\nTư Chất:<br />\r\n<br />\r\nB&igrave;nh Thường: Thi&ecirc;n&nbsp;Bạch&nbsp; ( Trắng) - Thuần&nbsp;Thanh (&nbsp;Xanh Lam) - Tử&nbsp;Th&uacute;y ( T&iacute;m)&nbsp;- B&igrave;nh Minh ( V&agrave;ng&nbsp;) - X&iacute;ch Diệm ( Đỏ)&nbsp;- Hắc &Aacute;m ( Đen&nbsp;) - X&iacute;ch Nguyệt ( Hỗn Hợp Của Đỏ V&agrave; Đen)<br />\r\n<br />\r\nHệ:<br />\r\n<br />\r\nBăng, Hoả, L&ocirc;i, Phong, &Aacute;nh S&aacute;ng, B&oacute;ng Tối, Sinh Mệnh, Tử Vong, Kh&ocirc;ng Gian, Thời Gian, T&igrave;nh Y&ecirc;u, Sắc Dục, Thi&ecirc;n Mệnh<br />\r\n<br />\r\nCấp Bậc:<br />\r\n<br />\r\nLuyện Hồn: Thường Nh&acirc;n - Linh Sĩ - Linh U&yacute; - Linh T&aacute; - Linh Tướng - Linh Vương - Linh Đế - Ch&uacute;a Tể - Thần - Tối Thượng.<br />\r\n<br />\r\nTu Ti&ecirc;n:Thường&nbsp;Nh&acirc;n -&nbsp;Luyện kh&iacute; - Tr&uacute;c cơ - Kết đan -&nbsp;Nguy&ecirc;n anh -&nbsp;H&oacute;a thần - Ph&aacute; Hư - Đế Vương - Độ Kiếp.<br />\r\n<br />\r\n( Mỗi cản giới ph&acirc;n l&agrave;m 10 cấp)</p>', '99-thanh-nien-oc-cho.jpg', '6llzeroll9', 0, 1, '2021-06-28 03:40:20', '2021-06-28 03:40:20'),
(29, 2, 'công chúa song sinh', 'cong-chua-song-sinh', '<p>Nội dung ch&iacute;nh của c&acirc;u chuyện l&agrave; kể về&nbsp;&nbsp;hai c&ocirc; C&ocirc;ng ch&uacute;a song sinh với hai số phận, từ l&uacute;c sinh ra đ&atilde; thất lạc nhau.<br />\r\n<br />\r\nVới đặc t&iacute;nh cũng kh&aacute;c nhau khi một người th&igrave; mang lại may mắn, C&ograve;n một người th&igrave; nắm giữ năng lực huyền b&iacute;, một sức mạnh khiến cho bao người đau khổ.<br />\r\n<br />\r\nLiệu c&oacute; C&ocirc;ng ch&uacute;a mang tr&ecirc;n m&igrave;nh sự đau khổ c&oacute; thể tự phong ấn ch&iacute;nh bản th&acirc;n m&igrave;nh kh&ocirc;ng??<br />\r\n<br />\r\nV&agrave; liệu c&ocirc; C&ocirc;ng ch&uacute;a c&oacute; biết được l&agrave; &ocirc;ng m&igrave;nh l&agrave; người đ&atilde; l&agrave;m cho m&igrave;nh thất lạc cha mẹ, v&agrave; Chị m&igrave;nh kh&ocirc;ng?? Để biết th&ecirc;m h&atilde;y theo d&otilde;i v&agrave; b&igrave;nh luận cho m&igrave;nh nh&eacute;!!</p>', '51-cong-chua-song-sinh.jpg', 'triệu hàn ngọc linh', 0, 1, '2021-06-28 03:41:00', '2021-06-28 03:41:00'),
(30, 2, 'dại hành ca', 'dai-hanh-ca', '<p><em>Thiếu ni&ecirc;n h&agrave;nh tẩu, đ&acirc;u ngờ lạc thi&ecirc;n sơn, sa v&agrave;o trầm lu&acirc;n<br />\r\nDung mạo thanh khiết, miết c&aacute;i gi&oacute; m&acirc;y thay, bao kẻ k&iacute;nh sợ<br />\r\nQuan ải vạn dặm lao đao, n&agrave;o sợ chi phải quay đầu&nbsp;<br />\r\nY&ecirc;u qu&aacute;i hại người nh&igrave;n chẳng lạ, l&agrave; m&acirc;y mưa vần tự ắt định<br />\r\nKh&uacute;c dạ h&agrave;nh ca, ta nghe c&ugrave;ng người</em><br />\r\n<br />\r\n***<br />\r\n<br />\r\nN&agrave;ng l&agrave; thiếu nữ t&agrave;i năng, tựa như đ&oacute;a mạn sa ch&acirc;u đến từ địa ngục, l&atilde;nh diễm m&agrave; rực rỡ.<br />\r\n<br />\r\nCh&agrave;ng l&agrave; thiếu ni&ecirc;n rơi v&agrave;o tuyệt cảnh phong hoa.<br />\r\n<br />\r\nN&agrave;ng v&igrave; b&aacute;o th&ugrave; m&agrave; lấy th&acirc;n thử độc, bốn m&ugrave;a qua đi dung mạo chưa từng thay đổi.<br />\r\n<br />\r\nCh&agrave;ng lại v&igrave; sinh tồn m&agrave; nhẫn nhục, cam chịu trở th&agrave;nh n&ocirc; lệ.<br />\r\nBảy năm s&aacute;t phạt đường sinh tử, ng&agrave;n dặm m&aacute;u đổ kh&oacute;i lửa loang<br />\r\n<br />\r\nHoa nhan Tử T&uacute;c, ng&agrave;n minh Cửu Vi<br />\r\n<br />\r\nC&ugrave;ng giương s&aacute;u c&aacute;nh thẳng phương Nam<br />\r\n<br />\r\nH&aacute; ngờ chuyện cũ Giang Nam lại r&agrave;ng t&acirc;m loạn, v&agrave;o sinh ra tử nay chẳng c&ograve;n đ&acirc;u, mỗi người chia nhau một mảng ch&acirc;n trời<br />\r\n<br />\r\nHương hoa sắp nhạt r&aacute;ng m&acirc;y sửa tan, hồng nhan nửa tận nhờ qu&acirc;n k&eacute;o, muốn nắm tay ngắm hoa đồng nở, lại chẳng biết phải thế n&agrave;o&hellip;</p>', '60-da-hanh-ca-tu-vi-luu-nien.jpg', 'tử vi lưu niên', 5, 1, '2021-06-28 03:41:49', '2021-06-28 04:20:01'),
(31, 2, 'hệ thống toàn năng tại thế giới', 'he-thong-toan-nang-tai-the-gioi', '<p>Nội dung truyện xoay quanh&nbsp;về một thanh ni&ecirc;n hậu đậu lại c&oacute; thể&nbsp;uống nhầm thuốc cũng bởi&nbsp;do nh&agrave; qu&aacute; tối nhưng lại bất ngờ được&nbsp;xuy&ecirc;n kh&ocirc;ng đến một nơi t&ecirc;n Thương Khung đại lục<br />\r\n<br />\r\nLăng Thi&ecirc;n cậu chết đi sống lại v&agrave; mang theo một bảng hệ thống b&aacute; đạo c&oacute; đầy đủ mọi thứ kỹ năng,trang bị,... Của rất nhiều thứ trong One Piece, Naruto,...<br />\r\n<br />\r\n- C&aacute;i g&igrave; l&agrave; căn cơ, l&agrave; thi&ecirc;n t&agrave;i?&nbsp;<br />\r\n<br />\r\n- Ta c&oacute; hệ thống th&igrave; bọn thi&ecirc;n t&agrave;i kh&ocirc;ng l&agrave; g&igrave; hết<br />\r\n<br />\r\n- C&aacute;c ngươi c&oacute; c&ocirc;ng ph&aacute;p cao cấp?<br />\r\n<br />\r\n- Hahaha. Ta cũng c&oacute;<br />\r\n<br />\r\n- C&aacute;c ngươi c&oacute; tiền t&agrave;i?<br />\r\n<br />\r\n- Ta kh&ocirc;ng c&oacute;...</p>', '90-he-thong-toan-nang-tai-di-gioi.jpg', 'Tadashi Ringo', 1, 1, '2021-06-28 03:44:22', '2021-06-28 08:18:24'),
(32, 2, 'hỗn thiên giới', 'hon-thien-gioi', '<p>Theo mọi người hiểu th&igrave; hỗn thi&ecirc;n giới l&agrave; g&igrave;? Đ&oacute; ch&iacute;nh l&agrave; một thế giới hỗn tạp, pha trộn bởi rất nh&igrave;u th&agrave;nh phần.<br />\r\n<br />\r\nỞ thời hiện đại th&igrave; c&oacute; một t&ecirc;n du c&ocirc;n L&acirc;m Ho&agrave;i lại chết trong l&uacute;c quay tay, linh hồn xuy&ecirc;n kh&ocirc;ng, bị buộc phải song tu c&ugrave;ng với một t&agrave;n hồn thượng cổ,..... V&agrave; bắt đầu con đường đạp ph&aacute; thi&ecirc;n hạ...</p>', '73-hon-thien-gioi.jpg', 'lạc kỳ nam', 4, 1, '2021-06-28 03:44:56', '2021-06-28 07:04:18'),
(33, 2, 'hắc ám thần đế', 'hac-am-than-de', '<p>rong một lần về qu&ecirc; nội th&igrave;&nbsp;Lưu V&acirc;n lại v&ocirc; t&igrave;nh nhặt được&nbsp;&nbsp;một đoạn&nbsp;kiếm rỉ s&eacute;t ở ven hồ v&agrave; dự t&iacute;nh&nbsp;đem về cho &ocirc;ng nội&nbsp;cậu xem. Trong đầu cậu cứ nghĩ &nbsp;l&agrave; đoạn&nbsp;kiếm c&ugrave;n, ai ngờ n&oacute; lại sắt b&eacute;n v&ocirc; c&ugrave;ng l&agrave;m cậu bị thương, m&aacute;u của Lưu V&acirc;n chảy dọc theo đoạn&nbsp;kiếm v&agrave; Lưu V&acirc;n bị h&uacute;t v&agrave;o v&ograve;ng xo&aacute;y thời kh&ocirc;ng.Hệ Th&ocirc;ng Tu Luyện: V&otilde; Đồ, V&otilde; Sĩ, V&otilde; Sư, V&otilde; Vương,V&otilde; Ho&agrave;ng, V&otilde;&nbsp;T&ocirc;ng, V&otilde; T&ocirc;n, V&otilde; Th&aacute;nh, V&otilde; Đế, v&agrave; được chia th&agrave;nh 10 tầng.<br />\r\n<br />\r\nHệ Thống Hồn Lực: Nhất cấp, Nhị Cấp, Tam Cấp... Cửu Cấp v&agrave; được chia l&agrave;m sơ kỳ, trung kỳ, hậu kỳ v&agrave; đỉnh phong.<br />\r\n<br />\r\nHệ Thống Y&ecirc;u Th&uacute;: Nhất cấp, Nhị Cấp, Tam Cấp... Cửu Cấp v&agrave; được chia l&agrave;m sơ kỳ, trung kỳ, hậu kỳ v&agrave; đỉnh phong.<br />\r\n<br />\r\n<strong>Lời n&oacute;i đầu:&nbsp;</strong>Ch&agrave;o tất cả c&aacute;c &quot;Đồng D&acirc;m&quot; &agrave; kh&ocirc;ng l&agrave; &quot;Đồng Đạo&quot;. Lời n&oacute;i đầu ti&ecirc;n xin cho ph&eacute;p Hắc &Aacute;m C&ocirc;ng Tử gửi đến c&aacute;c đồng đạo v&agrave; ban quản trị một năm tr&agrave;n đầy hạnh ph&uacute;c. Bộ truyện &quot;Hắc &Aacute;m Thần Đế&quot; l&agrave; bộ truyện đầu tay, n&ecirc;n trong qu&aacute; tr&igrave;nh s&aacute;ng t&aacute;c c&oacute; thể c&aacute;c bạn sẽ bắt gặp những khoảnh khắc cũng như những t&igrave;nh tiết c&oacute; thể giống hoặc kh&aacute; giống với c&aacute;c bộ truyện kh&aacute;c. V&agrave; tại hạ cam đoan l&agrave; tự m&igrave;nh s&aacute;ng t&aacute;c v&agrave; kh&ocirc;ng c&oacute; copy cũng nhưng đạo văn những bộ truyện kh&aacute;c nếu như c&oacute; t&igrave;nh huống tr&ecirc;n. V&igrave; thế mong được sự thương x&oacute;t cũng như sự n&acirc;ng đỡ từ c&aacute;c đồng đạo v&agrave; ban quản trị để t&aacute;c phẩm c&oacute; thể được ho&agrave;n thiện hơn. Xin cảm ơn.</p>', '86-hac-am-than-de (1).jpg', 'hắc đế', 3, 1, '2021-06-28 03:46:11', '2021-06-28 04:37:15'),
(34, 2, 'minh thần hệ thống', 'minh-than-he-thong', '<p><br />\r\nNội dung truyện xoay quanh về việc Qu&acirc;n Thường Minh mang theo b&aacute; ch&aacute;y hệ thống trở về qu&aacute; khứ. Liệu men ch&iacute;nh của ch&uacute;ng ta c&oacute; tung ho&agrave;ng tu ti&ecirc;u đ&ocirc; thị kh&ocirc;ng.....mỹ nữ..huynh đệ....sỉnh tử liệu Qu&acirc;n Thường Minh c&ugrave;ng Hệ thống xẽ ra sao.... Th&ocirc;i song t&aacute;c gi&aacute; đau bụng kh&ocirc;ng viết nổi nữa mọi người tự đọc đi ha....tạm biệt c&aacute;c huynh đệ v&agrave; c&ocirc; nương xinh đẹp he. Bye bye</p>', '43-minh-than-he-thong.jpg', 'tam thiên cửu', 2, 1, '2021-06-28 03:48:25', '2021-06-28 04:37:12'),
(35, 2, 'ma thần', 'ma-than', '<p><br />\r\nỞ thế kỷ hiện đại hắn l&agrave; một người gi&agrave;u c&oacute;, lại c&oacute; t&igrave;nh c&aacute;ch phong lưu cũng ch&iacute;nh v&igrave; thế m&agrave; bị ch&iacute;nh tay phụ nữ giết chết. Nhưng hắn lại được &ocirc;ng trời cho cơ hội xuy&ecirc;n kh&ocirc;ng&nbsp;đến một thế giới ho&agrave;n to&agrave;n kh&aacute;c nhập v&agrave;o một t&ecirc;n phế vật Lữ gia. Một thế giới tu luyện, thế giới cường giả vi t&ocirc;n.....</p>', '30-ma-than.jpg', 'minh phong', 9, 1, '2021-06-28 03:48:55', '2021-06-29 03:40:46'),
(36, 2, 'bất hủ kiếm thần', 'bat-hu-kiem-than', '<p>hời đại Th&aacute;i Cổ, Thi&ecirc;n Ma t&agrave;n s&aacute;t ch&uacute;ng sinh. Chứng kiến cảnh sinh linh đồ th&aacute;n, cường giả trăm tộc đ&atilde; hợp với nhau dựng l&ecirc;n một phương thế giới, chống chọi với Thi&ecirc;n Ma.</p>\r\n\r\n<p>Trận chiến Thần Ma đại ph&aacute;t, số Thần tham chiến ng&atilde; xuống qu&aacute; nửa nhưng đổi lại l&agrave; cuộc sống b&igrave;nh y&ecirc;n của trăm họ, năm th&aacute;ng an b&igrave;nh.</p>\r\n\r\n<p>Nghỉ dưỡng sức suốt vạn năm, trăm họ c&ugrave;ng đ&oacute;n nhận thời đại thịnh thế. Một t&ecirc;n tiểu tử th&acirc;n thể bất ph&agrave;m ngang trời xuất thế, hắn d&ugrave;ng sức một người x&ocirc;ng v&agrave;o Ma Vực, chiến ngoại tộc, l&ograve;ng mang ch&iacute;nh nghĩa, ph&aacute; vỡ thần thoại, trở th&agrave;nh một truyền kỳ bất hủ.</p>\r\n\r\n<p>Nhưng khi hắn đang đứng sừng sững ở tr&ecirc;n đỉnh phong ti&ecirc;n đồ, bước tr&ecirc;n hỏi &ocirc;ng trời th&igrave; lại biết được c&agrave;ng nhiều hơn về những đều mới lạ của Th&aacute;i Cổ v&agrave; b&iacute; mật kinh người. Một &iacute;t ch&acirc;n tướng dần dần trồi l&ecirc;n mặt nước, l&agrave;m cho cả người hắn ph&aacute;t lạnh...</p>\r\n\r\n<p>Ti&ecirc;n v&agrave; Ma vốn chỉ gần nửa bước,chẳng qua chỉ l&agrave; một &yacute; niệm....</p>', '74-bat-hu-kiem-than.jpg', 'tuyết mãn cung đao', 1, 1, '2021-06-28 04:31:27', '2021-06-28 04:36:54');

