INSERT INTO `chapters` (`ch_id`, `ch_book_id`, `ch_title`, `ch_slug`, `ch_content`, `ch_active`, `created_at`, `updated_at`) VALUES
(60, 17, 'chÆ°Æ¡ng 4: nhá»‹ ná»¯ tranh phu', 'chuong-4-nhi-nu-tranh-phu', '<p>Láº¡i qua v&agrave;i ng&agrave;y, Chu thá»‹ váº«n kh&ocirc;ng c&oacute; tháº¥y qua báº£n th&acirc;n phu qu&acirc;n.<br />\r\n<br />\r\n&Aacute;nh Ä‘&egrave;n má» nháº¡t, nh&igrave;n tr&ecirc;n b&agrave;n ch&eacute;n ch&aacute;o tráº¯ng nhá» c&ugrave;ng máº¥y m&oacute;n Äƒn, miá»‡ng n&agrave;ng nháº¡t tháº¿nh nhÆ° nÆ°á»›c á»‘c, má»™t ch&uacute;t kháº©u vá»‹ cÅ©ng kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nTháº¥y Chu thá»‹ váº«n cá»© kh&ocirc;ng c&oacute; kháº©u vá»‹, Tá»‘ng má»¥ tiáº¿n l&ecirc;n cÆ°á»i n&oacute;i: &quot;Phu nh&acirc;n, tá»‘t xáº¥u g&igrave; n&ecirc;n Äƒn má»™t ch&uacute;t Ä‘i? Nhá»‹ tá»· c&ograve;n chá» sá»¯a cá»§a ngÆ°Æ¡i Ä‘áº¥y!&quot;<br />\r\n<br />\r\nNghe Ä‘Æ°á»£c lá»i n&oacute;i Tá»‘ng má»¥, Chu thá»‹ cháº§n chá» má»™t ch&uacute;t, nhÆ°ng váº«n c&ograve;n láº¯c láº¯c Ä‘áº§u.&quot;Ta tháº­t sá»± l&agrave; Äƒn kh&ocirc;ng v&ocirc;, dáº¹p Ä‘i!&quot;<br />\r\n<br />\r\nCuá»‘i c&ugrave;ng Chu thá»‹ kh&ocirc;ng chá»‹u Äƒn cÆ¡m, kh&ocirc;ng chá»‹u uá»‘ng thuá»‘c, Tá»‘ng má»¥ Ä‘á»u d&ugrave;ng l&yacute; do nhá»‹ tá»· n&agrave;y tá»›i khuy&ecirc;n n&agrave;ng, tuy ráº±ng sáº½ kh&ocirc;ng t&aacute;c dá»¥ng tá»‘t nhÆ° trÆ°á»›c, nhÆ°ng tá»‘t xáº¥u g&igrave; n&agrave;ng c&ograve;n c&oacute; thá»ƒ Äƒn máº¥y pháº§n, nhÆ°ng h&ocirc;m nay ngay cáº£ n&agrave;y l&yacute; do n&agrave;y Ä‘á»u kh&ocirc;ng há»¯u hiá»‡u, Tá»‘ng má»¥ trong l&uacute;c nháº¥t thá»i Ä‘á»u kh&ocirc;ng biáº¿t khuy&ecirc;n nhÆ° tháº¿ n&agrave;o. Nh&igrave;n nh&igrave;n &aacute;nh máº¯t Chu thá»‹ c&oacute; ch&uacute;t dáº¡i ra, Tá»‘ng má»¥ biáº¿t h&ocirc;m nay khuy&ecirc;n cÅ©ng v&ocirc; dá»¥ng dá»©t kho&aacute;t liá»n ph&acirc;n ph&oacute; B&igrave;nh Nhi.&quot;B&igrave;nh Nhi, dá»n dáº¹p Ä‘i!&quot;<br />\r\n<br />\r\n&quot;NhÆ°ng l&agrave; phu nh&acirc;n má»™t Ä‘iá»ƒm cÅ©ng chÆ°a Äƒn!&quot; B&igrave;nh Nhi tiáº¿n l&ecirc;n nh&igrave;n nh&igrave;n tr&ecirc;n b&agrave;n cÄƒn báº£n l&agrave; Ä‘á»“ Äƒn kh&ocirc;ng há» 